public class HomeWork2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		// if - else if - else statement
		
		int hour = 8;
		
		if (hour < 12) {System.out.println("Good morning!"); }
		
		else if (hour <= 15) {System.out.println("Good afternoon");}  // checks if 'if' condition is not true, check new condition.
		
		else { System.out.println("Good Evening");}
// only one of 3 conditions is true.		

	}

}
